import React, { useState } from "react";

const daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

const categories = ["Category A", "Category B", "Category C"];

const CreateCompany = () => {
    const [logo, setLogo] = useState(null);
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");
    const [contacts, setContacts] = useState("");
    const [email, setEmail] = useState("");
    const [website, setWebsite] = useState("");
    const [fax, setFax] = useState("");
    const [description, setDescription] = useState("");
    const [category, setCategory] = useState(categories[0]);
    const [images, setImages] = useState([]);
    const [openingHours, setOpeningHours] = useState({});
    const [closingHours, setClosingHours] = useState({});

    const handleLogoChange = (event) => {
        setLogo(event.target.files[0]);
    };

    const handleNameChange = (event) => {
        setName(event.target.value);
    };

    const handleAddressChange = (event) => {
        setAddress(event.target.value);
    };

    const handleContactsChange = (event) => {
        setContacts(event.target.value);
    };

    const handleEmailChange = (event) => {
        setEmail(event.target.value);
    };

    const handleWebsiteChange = (event) => {
        setWebsite(event.target.value);
    };

    const handleFaxChange = (event) => {
        setFax(event.target.value);
    };

    const handleDescriptionChange = (event) => {
        setDescription(event.target.value);
    };

    const handleCategoryChange = (event) => {
        setCategory(event.target.value);
    };

    const handleImagesChange = (e) => {
        const files = Array.from(e.target.files);
        setImages(files);
      };

    const handleOpeningHoursChange = (event) => {
        const { name, value } = event.target;
        setOpeningHours((prev) => ({ ...prev, [name]: value }));
    };

    const handleClosingHoursChange = (event) => {
        const { name, value } = event.target;
        setClosingHours((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Submit form data to server
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <form style={{ maxWidth: 600, width: '100%', padding: '50px 20px', borderRadius: 10, boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 30 }}>
                    <div>
                        <label style={{ fontSize: 18, fontWeight: 'bold', marginBottom: 10 }}>
                            Company logo:
                            <input type="file" accept="image/*" onChange={handleLogoChange} style={{ marginLeft: 10 }} />
                        </label>
                    </div>
                    <div style={{ width: '50%', textAlign: 'right' }}>
                        <h1 style={{ margin: 0 }}>Add Company Details</h1>
                    </div>
                </div>
                <div style={{ display: 'flex', flexDirection: 'column', marginBottom: 30 }}>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company name:
                        <input type="text" value={name} onChange={handleNameChange} style={{ marginLeft: 10, padding: '5px 10px', borderRadius: 5, border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)', marginBottom: 10 }} />
                    </label>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company address:
                        <input type="text" value={address} onChange={handleAddressChange} style={{ marginLeft: 10, padding: '5px 10px', borderRadius: 5, border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)', marginBottom: 10 }} />
                    </label>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company contacts:
                        <input type="text" value={contacts} onChange={handleContactsChange} style={{ marginLeft: 10, padding: '5px 10px', borderRadius: 5, border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)', marginBottom: 10 }} />
                    </label>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company email:
                        <input type="email" value={email} onChange={handleEmailChange} style={{ marginLeft: 10, padding: '5px 10px', borderRadius: 5, border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)', marginBottom: 10 }} />
                    </label>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company website:
                        <input type="text" value={website} onChange={handleWebsiteChange} style={{ marginLeft: 10, padding: '5px 10px', borderRadius: 5, border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)', marginBottom: 10 }} />
                    </label>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company fax:
                        <input type="text" value={fax} onChange={handleFaxChange} style={{ marginLeft: 10, padding: '5px 10px', borderRadius: 5, border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)', marginBottom: 10 }} />
                    </label>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company description:
                        <textarea value={description} onChange={handleDescriptionChange} style={{ marginLeft: 10, padding: '10px', borderRadius: 5, border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)', marginBottom: 10 }} />
                    </label>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company category:
                        <select value={category} onChange={handleCategoryChange} style={{ marginLeft: 10, padding: '5px 10px', borderRadius: 5, border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)', marginBottom: 10 }}>
                            <option value="">Select category</option>
                            <option value="Technology">Technology</option>
                            <option value="Retail">Retail</option>
                            <option value="Healthcare">Healthcare</option>
                        </select>
                    </label>
                    <label style={{ fontSize: 16, fontWeight: 'bold', marginBottom: 10 }}>
                        Company/Stock images:
                        <input type="file" accept="image/*" multiple onChange={handleImagesChange} style={{ marginLeft: 10 }} />
                    </label>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <div style={{ width: '40%' }}>
                        <h2 style={{ fontSize: 18, fontWeight: 'bold', marginBottom: 10 }}>Opening hours:</h2>
                        <table style={{ width: '100%' }}>
                            <thead>
                                <tr>
                                    <th>Day</th>
                                    <th>Opening time</th>
                                    <th>Closing time</th>
                                </tr>
                            </thead>
                            <tbody>
                                {daysOfWeek.map(day => (
                                    <tr key={day}>
                                        <td>{day}</td>
                                        <td><input type="time" value={openingHours[day]} onChange={e => handleOpeningHoursChange(day, e.target.value)} /></td>
                                        <td><input type="time" value={closingHours[day]} onChange={e => handleClosingHoursChange(day, e.target.value)} /></td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    <div style={{ width: '50%', display: 'flex', flexDirection: 'column', justifyContent: 'flex-end', alignItems: 'flex-end' }}>
                        <button type="button" onClick={handleSubmit} style={{ padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', borderRadius: 5, border: 'none', cursor: 'pointer', marginBottom: 10 }}>Submit</button>
                        <button type="button"  style={{
                            padding: '10px 20px', backgroundColor: '#f44336', color: 'white', borderRadius: 5,
                            border: 'none', cursor: 'pointer'
                        }}>Reset</button>
                    </div>
                </div>
            </form>
            </div>
            );
}

            export default CreateCompany;
