import React, { useState,useEffect } from "react";
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8000',
});

function CreateProduct() {
    const [productData, setProductData] = useState({
        id: '',
        product_name: '',
        product_description: '',
        price: '',
        unit: '',
        video_url: '',
        tags: '',  
      });

      const handleInputChange = (event) => {
        const { name, value, type } = event.target;
        const newValue = type === 'file' ? event.target.files[0] : value;
        setProductData((prevFormData) => ({
          ...prevFormData,
          [name]: newValue,
        }));
      };

      function getCookie(name) {
        const cookieValue = document.cookie.match('(^|;)\\s*' + name + '\\s*=\\s*([^;]+)');
        return cookieValue ? cookieValue.pop() : '';
      }

    const handleSubmit=async (event)=>{
        event.preventDefault();

        const formData = new FormData(event.target);

        formData.append('product_name', event.target.company_name.value);
        formData.append('product_description', event.target.company_logo.files[0]);
        formData.append('price', event.target.company_address.value);
        formData.append('unit', event.target.company_phone.value);
        formData.append('video_url', event.target.company_email.value);
        formData.append('tags', event.target.company_fax.value);

        const csrftoken = getCookie('csrftoken');
        console.log(csrftoken)
        formData.append('csrfmiddlewaretoken', csrftoken);

        // Access form data using formData.get('field_name')
        // Perform actions with the form data, e.g., make an API request

        try {
            const response = await api.post('http://localhost:8000/company/company/', formData, {
              headers: {
                'X-CSRFToken': csrftoken,
                'Content-Type': 'multipart/form-data',
              },
            });

            // Handle the response here
            console.log(response);
          } catch (error) {
            // Handle errors here
            console.error(error);
          }
    }
    return ( 
        <>
        <div>
            <p>Create New Product</p>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name</label>
                    <input type="text" name="product_name" value={productData.product_name} onChange={handleInputChange} />
                </div>
                <div>
                    <label>Description</label>
                    <input type="text" name="product_description" value={productData.product_description} onChange={handleInputChange}/>
                </div>
                <div>
                    <label>Price</label>
                    <input type="text" name="price" value={productData.price} onChange={handleInputChange}/>
                </div>
                <div>
                    <label>Unit</label>
                    <input type="text" name="unit" value={productData.unit} onChange={handleInputChange} />
                </div>

                <div>
                    <label>Video</label>
                    <input type="text" name="video_url" value={productData.video_url} onChange={handleInputChange} />
                </div>
                <div>
                    <label>Tags</label>
                    <input type="text" name="tags" value={productData.tags} onChange={handleInputChange} />
                </div>

                <div>
                    <input type="submit" value="Submit" />
                </div>
            </form>
        </div>
        </>
     );
}

export default CreateProduct;
