import React, { useEffect, useState } from 'react';
import "./ShowPage.css";
import { Link } from 'react-router-dom';
import pic from './../assets/vendor-paper-logo.png';
import axios from 'axios';
import NavBar from '../components/NavBar';
import Carousel from 'react-bootstrap/Carousel';
import CategoryList from '../components/CategoryList';
import { Button } from 'bootstrap';


const api = axios.create({
    baseURL: 'http://localhost:8000',
});


export default function ShowPage() {
    const daysOfWeek = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
    const [companyInfo, setCompanyInfo] = useState({
        id: '',
        name: 'Company name',
        address: 'Company address',
        phone: '++977 9809211878',
        email: 'coolarshad96@gmailcom',
        website: 'http://noticevalley.com',
        fax: '+977 40023',
        description: '',
        logo: '',
        location: '',
        tags: '',
        images: [],
        categories: [],
        time: [],
        products: []
    });

    function capitalizeFirstLetter(string) {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get('/company/companyproduct/20/');
                const data = response.data;
                setCompanyInfo(data);
                console.log(data)
            } catch (error) {
                // Handle errors if necessary
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    const getProductImage = (product) => {
        const img = product.product_images?.[0];
        if (img) {
            return `http://localhost:8000${img.image}`
        }
        return pic;
    }

    const items = [{
        "Telecommunications and Computing": ['Computing and Technology',
            'Data Storage and Management',
            'Internet and E-Commerce',
            'Telecommunications and Networks']
    }, {
        "Pharmaceuticals": ['Anesthetic Drugs',
            'Animal Pharmaceuticals',
            'Antibiotics',
            'Biopharmaceuticals',
            'Clinical Trials',
            'Drug Delivery',
            'Drug Discovery',
            'Drugs by Therapeutic Area',
            'Generic Drugs',
            'Oncology Drugs',
            'Over the Counter (OTC) Drugs',
            'Pharmaceutical Intermediates',
            'Pharmaceutical Manufacturing',
            'Pharmacoeconomics',
            'Vitamins and Dietary Supplements']
    }]


    function calculateMaxItems(content) {
        const contentHeight = content.clientHeight;
        const itemHeight = content.querySelector('.items').clientHeight;

        // Calculate the maximum number of items that can fit
        const maxItems = Math.floor(contentHeight / itemHeight);

        return maxItems;
    }

    const accordionItems = document.querySelectorAll('.product-type-container');
    accordionItems.forEach((item) => {
        const header = item.querySelector('.product-type-header');
        const content = item.querySelector('.product-type-content');

        // header.addEventListener('click', () => {
        //     if (content.style.height === '250px') {
        //         content.style.height = '500px';
        //     } else {
        //         content.style.height = '250px';
        //     }
        // });
        header.addEventListener('click', () => {
            if (content.style.maxHeight) {
                content.style.maxHeight = null;
            } else {
                // Calculate and set the maximum height based on items
                const maxItems = calculateMaxItems(content);
                const itemHeight = content.querySelector('.items').clientHeight;
                const maxHeight = maxItems * itemHeight + 'px';
                content.style.maxHeight = maxHeight;
            }
        });
    });


    return (
        <>
            <NavBar />
            <div className='company-container'>
                <div className="homepage-left"></div>
                <div className="homepage-content">
                    <div className='content-top'>
                        <div className='category-section'>
                            <CategoryList items={items} />
                        </div>
                        <div className='carousel-section'>
                            <Carousel>
                                <Carousel.Item interval={1000}>
                                    <img className='carousel-img' src={pic} alt="First Slide" />
                                    <Carousel.Caption>
                                        <h3>First slide label</h3>
                                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                                <Carousel.Item interval={500}>
                                    <img className='carousel-img' src={pic} alt="Second Slide" />
                                    <Carousel.Caption>
                                        <h3>Second slide label</h3>
                                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                                <Carousel.Item>
                                    <img className='carousel-img' src={pic} alt="Third Slide" />
                                    <Carousel.Caption>
                                        <h3>Third slide label</h3>
                                        <p>
                                            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
                                        </p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                            </Carousel>
                        </div>
                    </div>
                    <div className='main-section'>
                        <div className='product-type-container'>
                            <div className='product-type-header'>
                                <p>Pharmaceuticals</p>
                                <button>More</button>
                            </div>
                            <div className='product-type-content'>
                                <div className='items'></div>
                                <div className='items'></div>
                                <div className='items'></div>
                                <div className='items'></div>
                                <div className='items'></div>
                                <div className='items'></div>
                                <div className='items'></div>
                                <div className='items'></div>
                                <div className='items'></div>
                            </div>
                            {/* <ProductTypeContainer/> */}
                        </div>
                    </div>
                    {/* <div className="extra-section"></div>
                <div className="main-section"></div> */}
                </div>
                <div className="homepage-right"></div>
            </div>
        </>

    )
}


